import React, { Component } from 'react'
import {
    Descriptions,
    Badge,
    Cascader,
    DatePicker,
    Input
} from 'antd';

const { TextArea } = Input;

//模拟数据
const options = [
    {
        value: '工商管理学院',
        label: '工商管理学院',
        children: [
            {
                value: '电子商务',
                label: '电子商务',
                children: [
                    {
                        value: '商务1811',
                        label: '商务1811',
                    },
                    {
                        value: '商务1812',
                        label: '商务1812',
                    }
                ],
            },
        ],
    },
    {
        value: '计算机学院',
        label: '计算机学院',
        children: [
            {
                value: '软件工程',
                label: '软件工程',
                children: [
                    {
                        value: '软工1812',
                        label: '软工1812',
                    },
                ],
            },
        ],
    },
];

export default class UserInfo extends Component {

    onChange(value) {
        console.log(value);
    }

    dateChange(value) {
        console.log(value);
    }

    render() {
        return (
            <div>
                <Descriptions title="" bordered={true} column={2}>
                    <Descriptions.Item label="姓名">
                        <Input placeholder="请填写兴趣姓名" />
                        {/* <TextArea rows={1} /> */}
                    </Descriptions.Item>
                    <Descriptions.Item label="性别">
                        <Input placeholder="请填写兴趣性别" />
                    </Descriptions.Item>
                    <Descriptions.Item label="联系方式">
                        <Input placeholder="请填写兴趣联系方式" />
                    </Descriptions.Item>
                    <Descriptions.Item label="兴趣爱好" span={2}>
                        <Input placeholder="请填写兴趣爱好" />
                    </Descriptions.Item>
                    <Descriptions.Item label="学院/专业/班级">
                        <Cascader
                            defaultValue={['学院', '专业', '班级']}
                            options={options}
                            onChange={this.onChange} />
                    </Descriptions.Item>
                    <Descriptions.Item label="生日">
                        <DatePicker onChange={this.dateChange} bordered={false} placeholder="请选择生日" />
                    </Descriptions.Item>
                    <Descriptions.Item label="自我评价" span={2}>
                        <TextArea rows={4} />
                    </Descriptions.Item>
                    <Descriptions.Item label="加入社团的期望" span={2}>
                        <TextArea rows={4} />
                    </Descriptions.Item>
                    <Descriptions.Item label="面试结果评价">
                        <Input placeholder="请填写兴趣爱好" />
                    </Descriptions.Item>
                    <Descriptions.Item label="纳新审核结果">
                        <Badge status="processing" text="审核中" />
                    </Descriptions.Item>
                </Descriptions>
            </div>
        )
    }
}
