import React, {useState} from 'react'
import {
    Menu,
    Layout,
    Form,
    Button,
    Radio,
    Select,
    DatePicker,
    InputNumber,
    TreeSelect,
    Switch,
    Input,
    Cascader,
} from 'antd';

import {
    UnlockOutlined,
    UserOutlined,
} from '@ant-design/icons';

import Particles from 'react-particles-js';

import './index.css'

const {Header, Content, Footer} = Layout;

export default function Login() {

    const onFinish = (values) => {
        console.log("==1 values", values)
    }

    return (
        <div className="loginBackGround">
            <Particles
                // 粒子效果高度为当前元素高度
                height={document.documentElement.clientHeight}
                params={
                    {
                        "background": {
                            "color": {
                                "value": "#ffffff"
                            },
                            "image": "url('https://particles.js.org/images/background3.jpg')",
                            "position": "50% 50%",
                            "repeat": "no-repeat",
                            "size": "cover"
                        },
                        "backgroundMask": {
                            "cover": {
                                "color": {
                                    "value": {
                                        "r": 255,
                                        "g": 255,
                                        "b": 255
                                    }
                                }
                            },
                            "enable": true
                        },
                        "fullScreen": {
                            "enable": true,
                            "zIndex": 1
                        },
                        "interactivity": {
                            "events": {
                                "onClick": {
                                    "enable": true,
                                    "mode": "push"
                                },
                                "onHover": {
                                    "enable": true,
                                    "mode": "bubble",
                                    "parallax": {
                                        "force": 60
                                    }
                                }
                            },
                            "modes": {
                                "bubble": {
                                    "distance": 400,
                                    "duration": 2,
                                    "opacity": 1,
                                    "size": 100
                                },
                                "grab": {
                                    "distance": 400
                                }
                            }
                        },
                        "particles": {
                            "color": {
                                "value": "#ffffff"
                            },
                            "links": {
                                "color": {
                                    "value": "#ffffff"
                                },
                                "distance": 150,
                                "enable": true
                            },
                            "move": {
                                "attract": {
                                    "rotate": {
                                        "x": 600,
                                        "y": 1200
                                    }
                                },
                                "enable": true,
                                "outModes": {
                                    "bottom": "out",
                                    "left": "out",
                                    "right": "out",
                                    "top": "out"
                                }
                            },
                            "number": {
                                "density": {
                                    "enable": true
                                },
                                "value": 80
                            },
                            "opacity": {
                                "animation": {
                                    "speed": 1,
                                    "minimumValue": 0.1
                                }
                            },
                            "size": {
                                "random": {
                                    "enable": true
                                },
                                "value": {
                                    "min": 1,
                                    "max": 30
                                },
                                "animation": {
                                    "speed": 40,
                                    "minimumValue": 0.1
                                }
                            }
                        }
                    }
                }
            />
            <div className="userFormContainer">
                <div className="loginTitle">高校社团管理系统</div>
                <Form labelCol={{span: 4,}}
                      onFinish={onFinish}
                >
                    <Form.Item name="userId">
                        <Input placeholder="请输入学号/职工号" id="userId" prefix={<UserOutlined />} />
                    </Form.Item>
                    <Form.Item name="password">
                        <Input placeholder="请输入密码" id="password" prefix={<UnlockOutlined />} />
                    </Form.Item>
                    <Form.Item>
                        <Button
                            type={"primary"}
                            htmlType={"submit"}
                            className={"login-form-button"}
                        >确定</Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}
