import React, { Component } from 'react'
import {
    Menu,
    Layout,
    Dropdown
} from 'antd';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    DownOutlined
} from '@ant-design/icons'
import { Avatar } from 'antd';
import { UserOutlined } from '@ant-design/icons';

const { Header } = Layout;
// 菜单
const menu = (
    <Menu>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="https://www.antgroup.com">
                1st menu item
      </a>
        </Menu.Item>
        <Menu.Item icon={<DownOutlined />} disabled>
            <a target="_blank" rel="noopener noreferrer" href="https://www.aliyun.com">
                2nd menu item (disabled)
      </a>
        </Menu.Item>
        <Menu.Item disabled>
            <a target="_blank" rel="noopener noreferrer" href="https://www.luohanacademy.com">
                3rd menu item (disabled)
      </a>
        </Menu.Item>
        <Menu.Item danger>退出系统</Menu.Item>
    </Menu>
);




export default class TopHeader extends Component {

    state = { collapsed: false }

    changeFoldState = () => {
        const { collapsed } = this.state
        this.setState({ collapsed: !collapsed })
    }

    render() {
        return (
            <Header className="site-layout-background" style={{ background: "white", padding: "0px 12px" }}>
                {this.state.collapsed ?
                    <MenuFoldOutlined onClick={this.changeFoldState} />
                    : <MenuUnfoldOutlined onClick={this.changeFoldState} />}

                &nbsp;&nbsp;&nbsp;

                首页

                <div style={{ float: "right" }}>
                    <sapn>欢迎 admin 回来~</sapn>
                    <Dropdown overlay={menu}>
                        <span className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                            <Avatar size={64} icon={<UserOutlined />} /> <DownOutlined />
                        </span>
                    </Dropdown>
                </div>
            </Header>
        )
    }
}
