import React, { useState, useEffect, useRef } from 'react'
import {
    Table,
    Button,
    Modal,
    Switch,
} from 'antd'

import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';

import axios from 'axios'
import UserForm from '../../../components/user-manage/UserForm';



const { confirm } = Modal

export default function UserList() {

    const [dataSource, setDataSource] = useState([])
    const [isAddModalVisible, setIsAddModalVisible] = useState(false)

    const [associationList, setAssciationList] = useState([])
    const [roleList, setRoleList] = useState([])

    const addFormRef = useRef(null)

    // 获取社团列表
    useEffect(() => {
        axios.get("/association/getAllAssociationList")
            .then(res => {
                const { data } = res.data
                // console.log("==52 roleList", data);
                setAssciationList(data)
            })
            .catch(err => {
                console.log("获取社团列表出错！", err);
            })
    }, [])

    // 获取角色列表
    useEffect(() => {
        axios.get("/association/getRoleList")
            .then(res => {
                const { data } = res.data
                // console.log("==52 roleList", data);
                setRoleList(data)
            })
            .catch(err => {
                console.log("获取角色列表出错！", err);
            })
    }, [])

    // 获取用户列表
    useEffect(() => {
        axios.get("/association/getAllUsers")
            .then(res => {
                const { data } = res.data
                // console.log("==51 userList", data);
                setDataSource(data)
            })
            .catch(err => {
                console.log("获取用户列表出错！", err);
            })
    }, [])

    // 表的行列结构
    const columns = [
        {
            title: '学号',
            dataIndex: 'userId',
            render: (userId) => {
                return <b>{userId}</b>
            }
        },
        {
            title: '姓名',
            dataIndex: 'name',
        },
        {
            title: '角色',
            dataIndex: 'roleList',
            render: (roleList) => {
                return roleList.map((role) => {
                    return (
                        <div key={role.roleId}>
                            {role.roleName} OF {role.associationName}
                        </div>
                    )
                })
            }
        },
        {
            title: '成员状态',
            dataIndex: 'state',
            render: ((state) => {
                return <Switch
                    checked={state === "OPEN"}
                >
                </Switch>
            })
        },
        {
            title: '操作',
            render: (item) => {
                return <div>
                    <Button
                        shape={"circle"}
                        icon={<UnorderedListOutlined />}
                        disabled={item.canDel === 0}
                    >
                    </Button>
                    &nbsp;&nbsp;&nbsp;
                    <Button
                        shape={"circle"}
                        icon={<DeleteOutlined />}
                        danger
                        disabled={item.canDel === 0}
                        onClick={confirmDelete}
                    >
                    </Button>
                </div>
            }
        },
    ]


    // 删除确认
    const confirmDelete = (item) => {
        const { roleList } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // 同步页面
                // console.log("==1 dataSource", roleList);
                const newList = roleList.filter(data => data.roleId !== item.roleId)
                thisPoint.setState({ roleList: newList })
                // 调用后端接口，同步后台数据库
                // axios.get(`/association/deleteRoleById/${item.roleId}`)
            },
            onCancel() {

            }
        })
    }

    // 弹出模态框
    const showAddModal = () => {
        console.log("==1 弹出模态框");
        setIsAddModalVisible(true)
    }


    // 模态框 确认 添加成员
    const handelUserAddModalOk = () => {
        console.log("====3 确定");
        addFormRef.current.validateFields().then(value => {
            setIsAddModalVisible(false)// 关闭模态框
            console.log("==4 ", value);

            // 同步后端数据库
            // 1.post到数据库中，自增长生成ID

            // 2.根据ID
            axios.post(`/association/addUser`, {
                userId: value.user_id,
                name: value.name,
                roleName: value.roleName,
                associationName: value.associationName
            })

        }).catch(err => {
            // setIsAddModalVisible(false)
            console.log("==5 ", err);
        })
    }

    return (
        <div>
            <Button
                type="primary"
                // shape="circle"
                onClick={showAddModal}
            >
                新增成员
            </Button>
            <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{
                    pageSize: 5
                }}
                rowKey={item => item.id}
            />
            <Modal
                visible={isAddModalVisible}
                title="创建新成员"
                okText="确认"
                cancelText="取消"
                onCancel={() => {
                    setIsAddModalVisible(false)
                    console.log("==3 取消");
                }}
                onOk={handelUserAddModalOk}
            >
                <UserForm
                    associationList={associationList}
                    roleList={roleList}
                    ref={addFormRef}
                ></UserForm>
            </Modal>
        </div>
    )

}
