import React, { Component } from 'react'
import { Table, Tag, Button } from 'antd'
import {
    EditOutlined,
    DeleteOutlined
} from '@ant-design/icons';
import axios from 'axios'


export default class PermissonList extends Component {

    state = {
        dataSource: [],
        popVisible: false,
        columns : [
            {
                title: 'ID',
                // 通过dataIndex映射VO字段
                dataIndex: 'key',
                // key: 'key',
                render: (key) => {
                    return <b>{key}</b>
                }
            },
            {
                title: '权限名称',
                dataIndex: 'title',
            },
            {
                title: '权限路径',
                dataIndex: 'routePath',
                render: (routePath) => {
                    return <Tag color={"orange"}>{routePath}</Tag>
                }
            },
            {
                title: '操作',
                render: () => {
                    return <div>
                        <Button
                            shape={"circle"}
                            icon={<EditOutlined />}
                            style={{
                                border: "1px solid blue",
                                color: "blue"
                            }}
                        >
                        </Button>
                        &nbsp;&nbsp;&nbsp;
                        <Popconfirm
                            title="Title"
                            visible={visible}
                            onConfirm={handleOk}
                            okButtonProps={{ loading: confirmLoading }}
                            onCancel={handleCancel}
                        >
                            <Button
                                shape={"circle"}
                                icon={<DeleteOutlined />}
                                danger
                                onClick={confirmDelete}
                            >
                            </Button>
                        </Popconfirm>
                    </div>
                }
            },
        ],
        
    }

    componentDidMount() {
        // 获取菜单信息
        axios.get("/association/getPermissionList").then(res => {
            const { data } = res.data // res.data还只是responseBody
            data.map((item) => {
                return item.children = (item.children.length === 0 ? '' : item.children)
            })
            // console.log("==1 ", data);
            this.setState({ dataSource: data })
            // console.log("==2 ", this.state.dataSource);
        })
    }

    showPopconfirm = () => {
        setVisible(true);
    };

    handleOk = () => {
        setConfirmLoading(true);
        setTimeout(() => {
            setVisible(false);
            setConfirmLoading(false);
        }, 2000);
    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        setVisible(false);
    };

    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.dataSource}
                    columns={columns}
                    pagination={{
                        pageSize: 5
                    }}
                />
            </div>
        )
    }


}
