import React, { useState, useEffect } from 'react'
import {
    Table,
    Button,
    Modal,
    Switch,
    Form,
    Input,
    Select
} from 'antd'
import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const { confirm } = Modal

const { Option } = Select

export default function UserList() {

    const [dataSource, setDataSource] = useState([])
    const [isAddModalVisible, setIsAddModalVisible] = useState(false)
    const [roleList, setRoleList] = useState([])

    // 获取用户列表
    useEffect(() => {
        axios.get("/association/getAllUsers")
            .then(res => {
                const { data } = res.data
                // console.log("==51 userList", data);
                setDataSource(data)
            })
    }, [])

    // 获取角色列表
    useEffect(() => {
        axios.get("/association/getRoleList")
            .then(res => {
                const { data } = res.data
                // console.log("==52 roleList", data);
                setRoleList(data)
            })
    }, [])

    const columns = [
        {
            title: '学号',
            dataIndex: 'userId',
            render: (userId) => {
                return <b>{userId}</b>
            }
        },
        {
            title: '姓名',
            dataIndex: 'name',
        },
        {
            title: '角色',
            dataIndex: 'roleList',
            render: (roleList) => {
                return roleList.map((role) => {
                    return (
                        <div key={role.roleId}>
                            {role.associationName}-
                            ({role.roleName})
                        </div>
                    )
                })
            }
        },
        {
            title: '成员状态',
            dataIndex: 'state',
            render: ((state) => {
                return <Switch
                    checked={state === "OPEN"}
                >
                </Switch>
            })
        },
        {
            title: '操作',
            render: (item) => {
                return <div>
                    <Button
                        shape={"circle"}
                        icon={<UnorderedListOutlined />}
                        disabled={item.canDel === 0}
                    >
                    </Button>
                    &nbsp;&nbsp;&nbsp;
                    <Button
                        shape={"circle"}
                        icon={<DeleteOutlined />}
                        danger
                        disabled={item.canDel === 0}
                    >
                    </Button>
                </div>
            }
        },
    ]

    const confirmDelete = (item) => {
        const { roleList } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // 同步页面
                // console.log("==1 dataSource", roleList);
                const newList = roleList.filter(data => data.roleId !== item.roleId)
                thisPoint.setState({ roleList: newList })
                // 调用后端接口，同步后台数据库
                // axios.get(`/association/deleteRoleById/${item.roleId}`)
            },
            onCancel() {

            }
        })
    }

    const showAddModal = () => {
        console.log("==1 弹出模态框");
        setIsAddModalVisible(true)
    }

    const handleRoleChange = () => {
        console.log("==1 选择角色");
    }



    return (
        <div>
            <Button
                type="primary"
                shape="circle"
                onClick={showAddModal}
            >
                新增成员
            </Button>
            <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{
                    pageSize: 5
                }}
                rowKey={item => item.id}
            />
            <Modal
                visible={isAddModalVisible}
                title="Create a new collection"
                okText="Create"
                cancelText="Cancel"
                onCancel={() => {
                    setIsAddModalVisible(false)
                    console.log("==3 取消");
                }}
                onOk={() => {
                    setIsAddModalVisible(false)
                    console.log("==3 确定");
                }}
            >
                <Form
                    layout="horizontal"
                >
                    <Form.Item
                        name="user_id"
                        label="学号"
                        rules={[{ required: true, message: '该项不得为空！' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        name="name"
                        label="姓名"
                        rules={[{ required: true, message: '该项不得为空！' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        name="association"
                        label="社团"
                    >
                        <Select placeholder="请选择社团" style={{ width: '100%' }} onChange={handleRoleChange}>
                            {
                                roleList.map((role) => {
                                    return <Option key={role.roleId} value={role.roleName}>{role.roleName}</Option>
                                })
                            }
                        </Select>
                    </Form.Item>
                    <Form.Item
                        name="role"
                        label="角色"
                    >
                        <Select placeholder="请选择角色" style={{ width: '100%' }} onChange={handleRoleChange}>
                            {
                                roleList.map((role) => {
                                    return <Option key={role.roleId} value={role.roleName}>{role.roleName}</Option>
                                })
                            }
                        </Select>
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    )

}
