import {
    PageHeader,
    Steps,
} from 'antd'

const { Step } = Steps;

export default function AddRecruitment() {
    return (
        <div>
            <PageHeader className="site-page-header" title="撰写纳新信息" subTitle="发挥你的文笔，吸引同学们的加入吧~"/>
            <Steps current={0}>
                <Step title="填写基本信息" description="This is a description." />
                <Step title="填写纳新内容" subTitle="将会在纳新栏向同学展示" description="This is a description." />
                <Step title="保存或提交" description="This is a description." />
            </Steps>
        </div>
    )
}
