import React, { useState, useEffect } from 'react'
import { Table, Button, Modal, Switch } from 'antd'
import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const { confirm } = Modal

export default function UserList() {

    const [dataSource, setDataSource] = useState([])
    

    useEffect(() => {
        axios.get("/association/getAllUsers")
            .then(res => {
                const {data} = res.data
                console.log("==51 userList", data);
                setDataSource(data)
            })
    }, [])

    const columns = [
        {
            title: '学号',
            dataIndex: 'userId',
            render: (userId) => {
                return <b>{userId}</b>
            }
        },
        {
            title: '姓名',
            dataIndex: 'name',
        },
        {
            title: '成员状态',
            dataIndex: 'state',
            render: (() => {
                return <Switch></Switch>
            })
        },
        {
            title: '操作',
            render: () => {
                return <div>
                    <Button
                        shape={"circle"}
                        icon={<UnorderedListOutlined />}
                    >
                    </Button>
                    &nbsp;&nbsp;&nbsp;
                    <Button
                        shape={"circle"}
                        icon={<DeleteOutlined />}
                        danger
                    >
                    </Button>
                </div>
            }
        },
    ]

    const confirmDelete = (item) => {
        const { roleList } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // 同步页面
                // console.log("==1 dataSource", roleList);
                const newList = roleList.filter(data => data.roleId !== item.roleId)
                thisPoint.setState({ roleList: newList })
                // 调用后端接口，同步后台数据库
                // axios.get(`/association/deleteRoleById/${item.roleId}`)
            },
            onCancel() {

            }
        })
    }

    const showAddModal = ()=>{
        console.log("==1 弹出模态框");

    }

    return (
        <div>
            <Button
                type="primary"
                shape="circle"
                onClick={showAddModal}
            >
                新增成员
                </Button>
            <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{
                    pageSize:5
                }}
                rowKey={item=>item.id}
            />
        </div>
    )

}
