import React, { Component } from 'react'
import { Table,Tag } from 'antd'
import axios from 'axios'


const columns = [
    {
        title: 'ID',
        // 通过dataIndex映射VO字段
        dataIndex: 'key',
        // key: 'key',
        render: (key) => {
            return <b>{key}</b>
        }
    },
    {
        title: '权限名称',
        dataIndex: 'title',
    },
    {
        title: '权限路径',
        dataIndex: 'routePath',
        render: (routePath) => {
            return <Tag color={"orange"}>{routePath}</Tag>
        }
    },
    {
        title: '操作',
        render: (routePath) => {
            return 
            (<div>
                <Tag color={"orange"}>{routePath}</Tag>
            </div>)
        }
    },
];

export default class PermissonList extends Component {

    state = {
        dataSource: []
    }

    componentDidMount() {
        // 获取菜单信息
        axios.get("/association/getPermissionList").then(res => {
            const { data } = res.data // res.data还只是responseBody
            // console.log("==1 ", data);
            this.setState({ dataSource: data })
            // console.log("==2 ", this.state.dataSource);
        })
    }

    render() {
        return (
            <div>
                <Table dataSource={this.state.dataSource} columns={columns} />
            </div>
        )
    }


}
