import React, {Component, useState} from 'react'
import {
    Menu,
    Layout,
    Form,
    Button,
    Radio,
    Select,
    DatePicker,
    InputNumber,
    TreeSelect,
    Switch,
    Input,
    Cascader,
} from 'antd';

const {Header, Content, Footer} = Layout;

export default function Login() {

    const [formSize, setFormSize] = useState('default');
    const onFormLayoutChange = ({size}) => {
        setFormSize(size);
    };

    const onFinish = (values) => {

    }

    return (
        <div
            style={{
                backgroundColor: `#FFF`,
                height: '100%',
            }}
        >
            <Form
                labelCol={{span: 4,}}
                wrapperCol={{span: 14,}}
                layout="horizontal"
                onValuesChange={onFormLayoutChange}
                size={formSize}
                onFinish={onFinish}
            >
                <Form.Item label="表单尺寸调整" name="size">
                    <Radio.Group>
                        <Radio.Button value="small">小</Radio.Button>
                        <Radio.Button value="default">默认</Radio.Button>
                        <Radio.Button value="large">大</Radio.Button>
                    </Radio.Group>
                </Form.Item>
                <Form.Item label="学号">
                    <Input/>
                </Form.Item>
                <Form.Item label="手机">
                    <Input/>
                </Form.Item>
                <Form.Item label="性别">
                    <Select>
                        <Select.Option value="male">男生</Select.Option>
                        <Select.Option value="female">女生</Select.Option>
                        <Select.Option value="hidden">保密</Select.Option>
                    </Select>
                </Form.Item>
                <Form.Item label="专业班级">
                    <Cascader
                        options={[
                            {
                                value: 'zhejiang',
                                label: '工商管理学院',
                                children: [
                                    {
                                        value: 'hangzhou',
                                        label: '电子商务',
                                        children: [
                                            {
                                                value: 'hangzhou',
                                                label: '商务1811',
                                            },
                                        ],
                                    },
                                ],
                            },
                        ]}
                    />
                </Form.Item>
                <Form.Item label="生日">
                    <DatePicker/>
                </Form.Item>
                <Form.Item label="隐藏个人信息" valuePropName="checked">
                    <Switch/>
                </Form.Item>
                <Form.Item label="确认提交？">
                    <Button>确定</Button>
                </Form.Item>
            </Form>
        </div>
        // <Layout
        //     className="layout"
        //     style={{
        //         backgroundColor:'orange',
        //         height:'100%',
        //     }}
        // >
        //     <Header style={{
        //         height: "15%"
        //     }}>
        //         <div className="logo"/>
        //         <Menu theme="light" mode="horizontal" defaultSelectedKeys={['2']}>
        //             <Menu.Item key="1">{`nav1`}</Menu.Item>
        //             <Menu.Item key="2">{`nav2`}</Menu.Item>
        //         </Menu>
        //     </Header>
        //
        //     <Content style={{
        //         padding: '30 50px',
        //     }}>
        //     </Content>
        //     <Footer style={{textAlign: 'center'}}>橘集UACS ©2022 高校社团管理 Created by Hangwei.Peng</Footer>
        // </Layout>
    )
}
