import React, { Component } from 'react'
import { Layout, Menu } from 'antd';
import {
    AppleFilled,
    PlusCircleTwoTone,
    HomeTwoTone,
    ContactsTwoTone,
    ClusterOutlined,
    BarsOutlined,
    CarryOutOutlined,
    TableOutlined
} from '@ant-design/icons';
import { withRouter } from 'react-router-dom'
import axios from 'axios'

import './index.css'

const { SubMenu } = Menu;
const { Sider } = Layout;

const iconList = {
    "/manage/home": <HomeTwoTone />,
    "/manage/user": <ContactsTwoTone />,
    "/manage/association": <ClusterOutlined />,
    "/manage/association/list": <TableOutlined />,
    "/manage/association/add": <PlusCircleTwoTone />,
    "/manage/permission": <CarryOutOutlined />,
    "/manage/permission/role/list": <BarsOutlined />,
    "/manage/permission/permission/list": <TableOutlined />
}

class SideMenu extends Component {

    state = {
        menuList: [],
        selectKeys: [],
        openKeys: []
    }

    getMenuList
    
    componentWillMount() {
        // 获取菜单信息
        axios.get("/association/getPermissionList").then(res => {
            const { data } = res.data
            // console.log("==1 ", data);
            this.setState({ menuList: data })
        })

        // ( 刷新后 )选择默认展开项、高亮项
        // props.location.pathname 取出当前页面的路径
        const path = this.props.location.pathname
        // console.log("==3", path);
        const openIndex = path.lastIndexOf("\/")
        // console.log("==3", openIndex);
        const openKeys = [path.substring(0, openIndex)]
        // console.log("==4", openKeys);
        const selectKeys = Array(this.props.location.pathname)
        // console.log("==5", selectKeys);

        this.setState({ openKeys })
        this.setState({ selectKeys })
        // console.log("==4", this.state);
    }

    // 检查是否是菜单元素。true才在菜单栏展示
    checkPageElement = (item) => {
        return "MENU_ELEMENT" === item.type ? true : false
    }

    renderMenu = (menuList) => {
        return menuList.map((item) => {
            if (item.children.length > 0 && this.checkPageElement(item)) {
                return <SubMenu key={item.routePath} title={item.title} icon={iconList[item.routePath]}
                >
                    {/* 递归 */}
                    {this.renderMenu(item.children)}
                </SubMenu>
            }
            return this.checkPageElement(item) && <Menu.Item key={item.routePath} onClick={() => {
                this.props.history.push(item.routePath)
            }}>{iconList[item.routePath]} {item.title}</Menu.Item>
        })
    }

    render() {
        return (
            <Sider theme={"dark"} width={200} collapsible={true} collapsed={false}>
                <div style={{ display: "flex", height: "100%", "flexDirection": "column" }}>
                    <div className="logo"><AppleFilled /> 橘集 <AppleFilled />
                        <br />高校社团管理系统
                </div>
                    <div style={{ flex: 1, "overflow": "auto" }}>
                        <Menu theme="light" mode="inline"
                            defaultSelectedKeys={this.state.selectKeys}
                            defaultOpenKeys={this.state.openKeys}
                            style={{ height: '100%', borderRight: 0 }}>
                            {this.renderMenu(this.state.menuList)}
                        </Menu>
                    </div>
                </div>
            </Sider>
        )
    }
}

export default withRouter(SideMenu)
