import React, { Component } from 'react'
import { Table, Button, Modal } from 'antd'
import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const { confirm } = Modal

export default class RoleList extends Component {

    state = {
        // 角色列表
        roleList: [],
        // 角色列表表头 及 各列组件效果
        columns: [
            {
                title: 'ID',
                dataIndex: 'roleId',
                render: (roleId) => {
                    return <b>{roleId}</b>
                }
            },
            {
                title: '角色名称',
                dataIndex: 'roleName',
            },
            {
                title: '操作',
                render: (item) => {
                    return <div>
                        <Button
                            shape={"circle"}
                            icon={<UnorderedListOutlined />}
                        >
                        </Button>
                        &nbsp;&nbsp;&nbsp;
                        <Button
                            shape={"circle"}
                            icon={<DeleteOutlined />}
                            danger
                            onClick={() => this.confirmDelete(item)}
                        >
                        </Button>
                    </div>
                }
            },
        ],
    }

    
    // 获取角色列表
    getRoleList = () => {
        axios.get("/association/getRoleList").then(res => {
            const roleData = res.data.data
            // console.log("==31 res", res.data.data);
            this.setState({ roleList: roleData })
        })
    }

    confirmDelete = (item) => {
        const { roleList } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // 同步页面
                console.log("==1 dataSource", roleList);
                const newList = roleList.filter(data => data.roleId !== item.roleId)
                thisPoint.setState({ roleList: newList })
                // 调用后端接口，同步后台数据库
                // axios.get(`/association/deleteRoleById/${item.roleId}`)
            },
            onCancel() {

            }
        })
    }

    componentDidMount() {
        // 获取角色列表 并 同步到state中
        this.getRoleList()
    }



    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.roleList}
                    columns={this.state.columns}
                    rowKey={(item) => item.roleId}
                >
                </Table>
            </div>
        )
    }
}
import { Tree } from 'antd';
const treeData = [
  {
    title: 'parent 1',
    key: '0-0',
    children: [
      {
        title: 'parent 1-0',
        key: '0-0-0',
        disabled: true,
        children: [
          {
            title: 'leaf',
            key: '0-0-0-0',
            disableCheckbox: true,
          },
          {
            title: 'leaf',
            key: '0-0-0-1',
          },
        ],
      },
      {
        title: 'parent 1-1',
        key: '0-0-1',
        children: [
          {
            title: (
              <span
                style={{
                  color: '#1890ff',
                }}
              >
                sss
              </span>
            ),
            key: '0-0-1-0',
          },
        ],
      },
    ],
  },
];

const Demo = () => {
  const onSelect = (selectedKeys, info) => {
    console.log('selected', selectedKeys, info);
  };

  const onCheck = (checkedKeys, info) => {
    console.log('onCheck', checkedKeys, info);
  };

  return (
    <Tree
      checkable
      defaultExpandedKeys={['0-0-0', '0-0-1']}
      defaultSelectedKeys={['0-0-0', '0-0-1']}
      defaultCheckedKeys={['0-0-0', '0-0-1']}
      onSelect={onSelect}
      onCheck={onCheck}
      treeData={treeData}
    />
  );
};

ReactDOM.render(<Demo />, mountNode);
