import React, { Component } from 'react'
import { Table, Button, Modal, Tree } from 'antd'
import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const { confirm } = Modal

const treeData = [
    {
        title: 'parent 1',
        key: '0-0',
        children: [
            {
                title: 'parent 1-0',
                key: '0-0-0',
                disabled: true,
                children: [
                    {
                        title: 'leaf',
                        key: '0-0-0-0',
                        disableCheckbox: true,
                    },
                    {
                        title: 'leaf',
                        key: '0-0-0-1',
                    },
                ],
            },
            {
                title: 'parent 1-1',
                key: '0-0-1',
                children: [
                    {
                        title: (
                            <span
                                style={{
                                    color: '#1890ff',
                                }}
                            >
                                sss
                            </span>
                        ),
                        key: '0-0-1-0',
                    },
                ],
            },
        ],
    },
]

export default class RoleList extends Component {

    state = {
        // 角色列表
        roleList: [],
        // 树形权限列表
        permissionList: [],
        // 角色列表表头 及 各列组件效果
        columns: [
            {
                title: 'ID',
                dataIndex: 'roleId',
                render: (roleId) => {
                    return <b>{roleId}</b>
                }
            },
            {
                title: '角色名称',
                dataIndex: 'roleName',
            },
            {
                title: '操作',
                render: (item) => {
                    return <div>
                        <Button
                            shape={"circle"}
                            icon={<UnorderedListOutlined />}
                            onClick={() => {
                                this.setState({ isModalVisible: true })
                            }}
                        >
                        </Button>
                        &nbsp;&nbsp;&nbsp;
                        <Button
                            shape={"circle"}
                            icon={<DeleteOutlined />}
                            danger
                            onClick={() => this.confirmDelete(item)}
                        >
                        </Button>
                    </div>
                }
            },
        ],
        isModalVisible: false
    }

    componentDidMount() {
        // 获取角色列表 并 同步到state中
        this.getRoleList()
        // 获取权限列表 并 同步到state中
        this.getPermissionList()
    }


    // 获取角色列表
    getRoleList = () => {
        axios.get("/association/getRoleList").then(res => {
            const roleData = res.data.data
            // console.log("==31 res", roleData);
            this.setState({ roleList: roleData })
        })
    }

    // 获取树形权限列表
    getPermissionList = () => {
        axios.get("/association/getPermissionList").then(res => {
            const permissionData = res.data.data
            console.log("==52 permissionData", permissionData);
            this.setState({ permissionList: permissionData })
        })
    }

    confirmDelete = (item) => {
        const { roleList } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // 同步页面
                console.log("==1 dataSource", roleList);
                const newList = roleList.filter(data => data.roleId !== item.roleId)
                thisPoint.setState({ roleList: newList })
                // 调用后端接口，同步后台数据库
                // axios.get(`/association/deleteRoleById/${item.roleId}`)
            },
            onCancel() {

            }
        })
    }

    // 模态框 点击ok
    handleOk = () => {


        this.setState({ isModalVisible: false })
        console.log("==51 ok");

    }

    // 模态框 点击cancel或者右上角x号
    handleCancel = () => {



        this.setState({ isModalVisible: false })
        console.log("==51 cancel");
    }



    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.roleList}
                    columns={this.state.columns}
                    rowKey={(item) => item.roleId}
                >
                </Table>
                <Modal title="分配权限"
                    visible={this.state.isModalVisible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}>
                    <Tree
                        checkable={true}
                        defaultCheckedKeys={['1', '0-0-1']}
                        treeData={this.state.permissionList}
                    />
                </Modal>
            </div>
        )
    }
}
