import React, { Component } from 'react'
import { Layout, Menu } from 'antd';
import {
    UserOutlined,
    LaptopOutlined,
    NotificationOutlined,
    AppleFilled,
} from '@ant-design/icons';
import { withRouter } from 'react-router-dom'
import axios from 'axios'

import './index.css'

const { SubMenu } = Menu;
const { Sider } = Layout;

// 模拟菜单数组
const menuList = [
    {
        key: "/manage/home",
        title: "首页",
        icon: <UserOutlined />
    },
    {
        key: "/manage/association",
        title: "社团管理",
        icon: <NotificationOutlined />,
        children: [
            {
                key: "/manage/association/list",
                title: "社团列表",
                icon: <LaptopOutlined />
            },
            {
                key: "/manage/association/add",
                title: "创建社团",
                icon: <UserOutlined />
            }
        ]
    },
    {
        key: "/manage/permission",
        title: "权限管理",
        icon: <UserOutlined />,
        children: [
            {
                key: "/manage/permission/role/list",
                title: "角色列表",
                icon: <UserOutlined />
            },
            {
                key: "/manage/permission/permission/list",
                title: "权限列表",
                icon: <UserOutlined />
            }
        ]
    }
];





class SideMenu extends Component {

    state = {
        menuList:[]
    }

    componentWillMount(){
        axios.get("/association/getPermissionList").then(res=>{
            const {menuList} = res.data.data
            this.setState({menuList})
        })
        log
    }

    componentDidMount(){
        this.getPermission()
    }

    // getPermission = ()=>{
    //     axios.get("/association/getPermissionList").then(res=>{
    //         const {menuList} = res.data.data
    //         this.setState({menuList})
    //     })
    // }

    renderMenu = (menuList) => {
        return menuList.map((item) => {
            if (item.children) {
                return <SubMenu key={item.key} title={item.title} icon={item.icon}
                >
                    {/* 递归 */}
                    {this.renderMenu(item.children)}
                </SubMenu>
            }
            return <Menu.Item key={item.key} onClick={() => {
                this.props.history.push(item.key)
            }}>{item.icon} {item.title}</Menu.Item>
        })
    }

    render() {
        this.getPermission()
        return (
            <Sider width={200} collapsible={true} collapsed={false}>
                <div className="logo"><AppleFilled /> 橘集 <AppleFilled />
                    <br />高校社团管理系统
                </div>
                <Menu theme="dark" mode="inline"
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    style={{ height: '100%', borderRight: 0 }}>
                    {this.renderMenu(this.state.menuList)}
                </Menu>
            </Sider>
        )
    }
}

export default withRouter(SideMenu)
