import React, {useState} from 'react'
import {
    Menu,
    Layout,
    Form,
    Button,
    Radio,
    Select,
    DatePicker,
    InputNumber,
    TreeSelect,
    Switch,
    Input,
    Cascader,
} from 'antd';

import {
    UnlockOutlined,
    UserOutlined,
} from '@ant-design/icons';

import Particles from 'react-particles-js';

import './index.css'

const {Header, Content, Footer} = Layout;

export default function Login() {

    const onFinish = (values) => {
        console.log("==1 values", values)
    }

    return (
        <div className="loginBackGround">
            <Particles
                // 粒子效果高度为当前元素高度
                height={document.documentElement.clientHeight}
                params={
                    {
                        "background": {
                            "color": {
                                "value": "#ffffff"
                            },
                            "position": "50% 50%",
                            "repeat": "no-repeat",
                            "size": "cover"
                        },
                        "fullScreen": {
                            "enable": true,
                            "zIndex": 1
                        },
                        "particles": {
                            "color": {
                                "value": [
                                    "#5bc0eb",
                                    "#fde74c",
                                    "#9bc53d",
                                    "#e55934",
                                    "#fa7921"
                                ]
                            },
                            "move": {
                                "direction": "top",
                                "enable": true,
                                "outModes": {
                                    "bottom": "out",
                                    "left": "out",
                                    "right": "out",
                                    "top": "out"
                                },
                                "speed": 5
                            },
                            "number": {
                                "value": 30
                            },
                            "opacity": {
                                "random": {
                                    "enable": true,
                                    "minimumValue": 0.4
                                },
                                "value": {
                                    "min": 0.4,
                                    "max": 0.8
                                },
                                "animation": {
                                    "speed": 1,
                                    "minimumValue": 0.1
                                }
                            },
                            "size": {
                                "random": {
                                    "enable": true,
                                    "minimumValue": 300
                                },
                                "value": {
                                    "min": 300,
                                    "max": 400
                                },
                                "animation": {
                                    "enable": true,
                                    "speed": 30,
                                    "minimumValue": 300
                                }
                            }
                        }
                    }
                }
            />
            <div className="userFormContainer">
                <div className="loginTitle">高校社团管理系统</div>
                <Form labelCol={{span: 4,}}
                      onFinish={onFinish}
                >
                    <Form.Item name="userId">
                        <Input placeholder="请输入学号/职工号" id="userId" prefix={<UserOutlined/>}/>
                    </Form.Item>
                    <Form.Item name="password">
                        <Input placeholder="请输入密码" id="password" prefix={<UnlockOutlined/>}/>
                    </Form.Item>
                    <Form.Item>
                        <Button
                            type={"primary"}
                            htmlType={"submit"}
                            className={"login-form-button"}
                        >确定</Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}
