import React, { Component } from 'react'
import { Table } from 'antd'
import axios from 'axios'

export default class RoleList extends Component {

    state = {
        // 角色列表
        roleList: [],
        // 角色列表表头 及 各列组件效果
        columns: [
            {
                title: 'ID',
                dataIndex: 'roleId',
                render: (roleId) => {
                    return <b>{roleId}</b>
                }
            },
            {
                title: '角色名称',
                dataIndex: 'roleName',
            },
            {
                title: '操作',
                render: (item) => {
                    return <div>
                        <Popover
                            style={{}}
                            content={
                                <div>
                                    <Switch checked={item.type === "MENU_ELEMENT"} onClick={() => this.changeMenuState(item)}></Switch>
                                </div>}
                            title="是否在左侧菜单栏展示"
                            trigger={
                                item.type === "MENU_ELEMENT" || item.type === "MENU_ELEMENT_CLOSE" ?
                                    "click"
                                    : ""
                            }
                        >
                            {/* {item.type === "MENU_ELEMENT" &&} */}
                            <Button
                                shape={"circle"}
                                icon={<EditOutlined />}
                            // style={item.type === "MENU_ELEMENT" ? {
                            //     border: "1px solid rgb(165, 99, 13)",
                            //     color: "rgb(165, 99, 13)"
                            // } : { border: "1px" }}
                            // disabled={item.type === "PAGE_ELEMENT"}
                            >
                            </Button>
                        </Popover>
                        &nbsp;&nbsp;&nbsp;
                        <Button
                            shape={"circle"}
                            icon={<DeleteOutlined />}
                            danger
                            onClick={() => this.confirmDelete(item)}
                        >
                        </Button>
                    </div>
                }
            },
        ],
    }

    componentDidMount() {
        // 获取角色列表 并 同步到state中
        this.getRoleList()
    }


    // 获取角色列表
    getRoleList = () => {
        axios.get("/association/getRoleList").then(res => {
            const roleData = res.data.data
            console.log("==31 res", res.data.data);

            this.setState({ roleList: roleData })
        })
    }

    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.roleList}
                    columns={this.state.columns}
                    rowKey={(item) => item.roleId}
                >
                </Table>
            </div>
        )
    }
}
