import React, { Component } from 'react'


import { Layout, Menu } from 'antd';
import {
    UserOutlined,
    LaptopOutlined,
    NotificationOutlined,
    AppleFilled,
    BankOutlined
} from '@ant-design/icons';

import './index.css'

const { SubMenu } = Menu;
const { Sider } = Layout;

// 模拟菜单数组
const menuList = [
    {
        key: "/home",
        title: "首页",
        icon: <UserOutlined />
    },
    {
        key: "/association",
        title: "社团管理",
        icon: <UserOutlined />,
        children: [
            {
                key: "/list",
                title: "社团列表",
                icon: <UserOutlined />
            },
            {
                key: "/add",
                title: "创建社团",
                icon: <UserOutlined />
            }
        ]
    },
    {
        key: "/permission",
        title: "权限管理",
        icon: <UserOutlined />,
        children: [
            {
                key: "/role/list",
                title: "角色列表",
                icon: <UserOutlined />
            },
            {
                key: "/permission/list",
                title: "权限社团",
                icon: <UserOutlined />
            }
        ]
    }
];




export default class SideMenu extends Component {

    renderMenu = (menuList) => {
        return menuList.map((item)=>{
            if(item.children){
                return <SubMenu key={item.key} title={item.title} icon={item.icon}></SubMenu>
            }
            return <Menu.Item key={item.key} title={item.title} icon={item.icon}></Menu.Item>
        })
    }

    render() {
        return (
            <Sider width={200} collapsible={true} collapsed={false}>
                <div className="logo"><AppleFilled /> 橘集 <AppleFilled />
                    <br />高校社团管理系统
                </div>
                <Menu theme="dark" mode="inline"
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    style={{ height: '100%', borderRight: 0 }}>
                    {/* SubMenu:可折叠的菜单 */}
                    {/* <Menu.Item title="首页" style={{ textAlign: "left" }}>
                        <BankOutlined />&nbsp;&nbsp;&nbsp;首页
                    </Menu.Item>
                    <SubMenu key="sub1" icon={<UserOutlined />} title="社团管理">
                        <Menu.Item key="1">社团列表</Menu.Item>
                        <Menu.Item key="2">创建社团</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" icon={<UserOutlined />} title="角色管理">
                        <Menu.Item key="3">社团列表</Menu.Item>
                        <Menu.Item key="4">创建社团</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub3" icon={<UserOutlined />} title="权限管理">
                        <Menu.Item key="5">社团列表</Menu.Item>
                        <Menu.Item key="6">创建社团</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub4" icon={<LaptopOutlined />} title="审核管理">
                        <Menu.Item key="7">审核纳新</Menu.Item>
                        <Menu.Item key="8">审核退社</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub5" icon={<NotificationOutlined />} title="杂项管理">
                        <Menu.Item key="9">呼呼呼</Menu.Item>
                        <Menu.Item key="10">啦啦啦</Menu.Item>
                        <Menu.Item key="11">哈哈哈</Menu.Item>
                        <Menu.Item key="12">嘻嘻嘻</Menu.Item>
                    </SubMenu> */}

                    {this.renderMenu(menuList)}


                </Menu>
            </Sider>
        )
    }
}
