import React, { Component } from 'react'
import { Table, Tag, Button, Modal } from 'antd'
import {
    EditOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const columns = [
    {
        title: 'ID',
        // 通过dataIndex映射VO字段
        dataIndex: 'key',
        // key: 'key',
        render: (key) => {
            return <b>{key}</b>
        }
    },
    {
        title: '权限名称',
        dataIndex: 'title',
    },
    {
        title: '权限路径',
        dataIndex: 'routePath',
        render: (routePath) => {
            return <Tag color={"orange"}>{routePath}</Tag>
        }
    },
    {
        title: '操作',
        render: (item) => {
            return <div>
                <Button
                    shape={"circle"}
                    icon={<EditOutlined />}
                    style={{
                        border: "1px solid blue",
                        color: "blue"
                    }}
                >
                </Button>
                &nbsp;&nbsp;&nbsp;
                <Button
                    shape={"circle"}
                    icon={<DeleteOutlined />}
                    danger
                    onClick={()=>confirmDelete(item)}
                >
                </Button>
            </div>
        }
    },
]

const { confirm } = Modal

function confirmDelete(item){
    confirm({
        title:'您确认要删除吗？',
        icon:<ExclamationCircleOutlined/>,
        onOk(item){
            console.log("==10 item",item);
        },
        onCancel(){

        }
    })
}


export default class PermissonList extends Component {

    state = {
        dataSource: [],
    }


    componentDidMount() {
        // 获取菜单信息
        axios.get("/association/getPermissionList").then(res => {
            const { data } = res.data // res.data还只是responseBody
            data.map((item) => {
                return item.children = (item.children.length === 0 ? '' : item.children)
            })
            // console.log("==1 ", data);
            this.setState({ dataSource: data })
            // console.log("==2 ", this.state.dataSource);
        })
    }

    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.dataSource}
                    columns={columns}
                    pagination={{
                        pageSize: 5
                    }}
                />
            </div>
        )
    }


}
