import React, { Component } from 'react'
import { Table, Tag, Button } from 'antd'
import {
    EditOutlined,
    DeleteOutlined
} from '@ant-design/icons';
import axios from 'axios'


export default class PermissonList extends Component {

    state = {
        dataSource: [],
        popVisible: false
    }

    componentDidMount() {
        // 获取菜单信息
        axios.get("/association/getPermissionList").then(res => {
            const { data } = res.data // res.data还只是responseBody
            data.map((item) => {
                return item.children = (item.children.length === 0 ? '' : item.children)
            })
            // console.log("==1 ", data);
            this.setState({ dataSource: data })
            // console.log("==2 ", this.state.dataSource);
        })
    }

    showPopconfirm = () => {
        setVisible(true);
    };

    handleOk = () => {
        setConfirmLoading(true);
        setTimeout(() => {
            setVisible(false);
            setConfirmLoading(false);
        }, 2000);
    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        setVisible(false);
    };

    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.dataSource}
                    columns={columns}
                    pagination={{
                        pageSize: 5
                    }}
                />
            </div>
        )
    }


}
