import {
    PageHeader,
    Steps,
} from 'antd'

const { Step } = Steps;

export default function AddRecruitment() {
    return (
        <div>
            <PageHeader className="site-page-header" title="撰写纳新信息" subTitle="发挥你的文笔，吸引同学们的加入吧~"/>
            <Steps current={0}>
                <Step title="基本信息" description="纳新人数，起始时间等" />
                <Step title="展示内容" description="纳新通知的信息内容等" />
                <Step title="保存/提交" description="仅保存等待再次编辑或提交等待审核" />
            </Steps>
        </div>
    )
}
