import React, {useState} from 'react'
import {
    Menu,
    Layout,
    Dropdown,
    Avatar
} from 'antd';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    DownOutlined,
    UserOutlined,
    CloseOutlined
} from '@ant-design/icons'

const {Header} = Layout;


// 菜单数据
const menu = (
    <Menu>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="https://www.antgroup.com">
                超级管理员
            </a>
        </Menu.Item>
        <Menu.Item danger onClick={()=>{

        }}><CloseOutlined/>退出系统</Menu.Item>
    </Menu>
);


export default function TopHeader() {

    const [collapsed, setCollapsed] = useState(false)

    const changeFoldState = () => {
        const {collapsed} = this.state
        this.setState({collapsed: !collapsed})
    }

        return (
            <Header className="site-layout-background" style={{background: "white", padding: "0px 12px"}}>
                {this.state.collapsed ?
                    <MenuFoldOutlined onClick={this.changeFoldState}/>
                    : <MenuUnfoldOutlined onClick={this.changeFoldState}/>}

                &nbsp;&nbsp;&nbsp;

                首页

                <div style={{float: "right"}}>
                    欢迎 admin 回来~
                    &nbsp;&nbsp;&nbsp;
                    <Dropdown overlay={menu}>
                        <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                            <Avatar size={40} icon={<UserOutlined/>}/>
                            <DownOutlined/>
                        </a>
                    </Dropdown>
                </div>
            </Header>
        )
}
