import React, { Component } from 'react'
import { Table,Button } from 'antd'
import {
    UnorderedListOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined
} from '@ant-design/icons';
import axios from 'axios'

const { confirm } = Modal

export default class RoleList extends Component {

    state = {
        // 角色列表
        roleList: [],
        // 角色列表表头 及 各列组件效果
        columns: [
            {
                title: 'ID',
                dataIndex: 'roleId',
                render: (roleId) => {
                    return <b>{roleId}</b>
                }
            },
            {
                title: '角色名称',
                dataIndex: 'roleName',
            },
            {
                title: '操作',
                render: (item) => {
                    return <div>
                        <Button
                            shape={"circle"}
                            icon={<UnorderedListOutlined />}
                        >
                        </Button>
                        &nbsp;&nbsp;&nbsp;
                        <Button
                            shape={"circle"}
                            icon={<DeleteOutlined />}
                            danger
                            onClick={() => this.confirmDelete(item)}
                        >
                        </Button>
                    </div>
                }
            },
        ],
    }

    confirmDelete = (item) => {
        // const { dataSource } = this.state
        // console.log("==11 dataSource",this.state.dataSource);
        const { dataSource } = this.state
        const thisPoint = this
        confirm({
            title: '您确认要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // console.log("==10 item", item);
                // 同步页面
                let ds = []
                // let ds = Array.from(dataSource)
                if (item.grade === 1) {
                    // console.log("==11 ", ds);
                    ds = dataSource.filter(data => data.key !== item.key)
                } else if (item.grade === 2) {
                    // console.log("==12 ", ds);
                    dataSource.map((firstPermission) => {
                        if (firstPermission.children !== "") {
                            firstPermission.children = firstPermission.children.filter((child) => child.key !== item.key)
                            if (firstPermission.children.length === 0) {
                                firstPermission.children = ""
                            }
                            ds.push(firstPermission)
                            // console.log("==14 after push:", ds);
                        } else {
                            ds.push(firstPermission)
                        }

                    })
                }
                thisPoint.setState({ dataSource: ds })
                // 调用后端接口，同步后台数据库
                axios.get(`/association/deletePermissionById/${item.key}`)
            },
            onCancel() {

            }
        })
    }

    componentDidMount() {
        // 获取角色列表 并 同步到state中
        this.getRoleList()
    }


    // 获取角色列表
    getRoleList = () => {
        axios.get("/association/getRoleList").then(res => {
            const roleData = res.data.data
            console.log("==31 res", res.data.data);

            this.setState({ roleList: roleData })
        })
    }

    render() {
        return (
            <div>
                <Table
                    dataSource={this.state.roleList}
                    columns={this.state.columns}
                    rowKey={(item) => item.roleId}
                >
                </Table>
            </div>
        )
    }
}
